	XtSetArg(al[ac], XmNrightAttachment, XmATTACH_FORM); ac++;
	XtSetArg(al[ac], XmNrightOffset, 10); ac++;
	XtSetValues ( productST, al, ac );
	ac = 0;
	if (productTxt != (Widget) 0) { XtManageChild(productTxt); }
	if ((children[ac] = prodviewFO) != (Widget) 0) { ac++; }
	if ((children[ac] = prodtxtFM) != (Widget) 0) { ac++; }
